sources.sass/_seting.scss:
$color-1 : #FF1818;
$color-2 : #1e8018;
$color-3 : #d3d3d3;
$color-4 : rgb(255, 166, 0);

body{
    font-family: 'Courier New', Courier, monospace;
    margin: 0;
    padding: 0;
}
#app{
    display: grid;
    grid-template-columns: 25% 1fr;
    grid-template-areas: "A B";
}
#main{
    grid-area: B;
}

ul{
   list-style: none;
}

sources.sass/_menu.scss:
#menu {
    grid-area: A;
    width: 25%;
    min-width: 250px;
    height: 100vh;
    margin: 0;
    background:$color-3;
    position: fixed;
    display:grid;
    justify-content: center;
    grid-template-rows: 10% 1fr 30% ;
    z-index: 1;
    .title{
        h1{
            font-weight:100;
            margin-bottom: 0;
            font-size: 2rem;
            span{
                font-weight: bold !important;
                color: $color-1;
            }
        }
        article{
            letter-spacing: 0.2px;
            word-spacing: 0px;
            color: $color-2;
            font-size: 1rem;

        }
    }
    .menu{
        display: flex;
        flex-direction: column;
        justify-content: center;
        a{
            text-decoration: none;
            padding:1rem;
            margin: 10px 0;
            transition: 0.5s ease-in-out;
            &:hover{
                background: $color-4;
                color: #000;
            }
        }
    }
    .footer{
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        font-size: 0.8rem;
        a{
            color: $color-2;
            &:hover{
                color: $color-1;
            }
        }
        span{
            width: inherit;
            .signature{
                margin-bottom: 10px;
                text-align: center;
                font-weight: bold;
            }
            a{
                font-size: 2rem;
                margin: 1rem;
            }
        }
    }
}

sources.sass/_tirage.scss:
.tirage{
    @include flex-center-column;
    .search{
        min-width: 400px;
        background:$color-4;
        padding: 1rem;
        margin: 10px 0;
        right: 0;
        p{
            margin: 10px 0 0 0;
            font-size: 1rem;
            color: $color-2;
            opacity: 0.9;
        }
        span{
            margin: 10px;
            input[type ="text"]{
                @include input;
                border-radius: 50px 0 0 50px;
                background: $color-3;
                letter-spacing: 0.1rem;
                color: $color-2;
            }
            input[type ="button"]{
               @include input;
               border-radius: 0 50px 50px 0px;
               background: black;
               color: $color-3;
               cursor: pointer;
               &:hover{
                transform: scale(1.1)
               }
               &:active{
                transform: scale(1)
               }
            }
        }
    }

}
.cocktail-contenaire{
    display: flex;
    flex-wrap: wrap;
    list-style: none;
    justify-content: center;
    align-items: center;
    .cocktail-eacth{
        margin: 3px;
        padding: 5px;
        background: $color-3;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        p{
            font-size: 0.8rem;
            font-weight: bold;
        }
        h1{
            color: $color-1;
            font-size: 1rem;
            margin: 0;
        }
        .name-contenaire{
            @include flex-center-column;
            align-items: center;
            justify-content: center;

        }
        strong{
            color: $color-2;
            text-decoration: underline;
        }
        &:hover{
            transform: scale(1.02);
            background: $color-4;
            h1{
                color: black;
            }
        }
        img{
            width: 150px;
            height: 150px;
            margin: 5px;
        }
    }

}
.resultError{
    color: $color-1;
    span{
        font-weight: 100;
        color: $color-4;
        font-size: 1.2rem;
    }
}
//tirage aux hasards
.hasard{
    height: 100vh;
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    // align-items: center;
    h1{
        color: $color-2;
    }
    .basic{
        background: $color-3;
        padding:0 2rem;
        transition: 0.5s ease-in-out;
        @include flex-center-column;
        strong{
            color: $color-1;
            font-size: 1rem;
            margin: 0;
        }

        img{
            width: 400px;
            height: 400px;

            left: 50%;
        }
        &:hover{
            background: #000;
            h1,h2,h3{
                color: #fff;
            }
        }
    }
    .ingredient{
        background:$color-4;
        padding: 1rem;
        height: auto;
        h1{
            font-size: 1rem;

            position: relative;
        }
    }
}

sources.sass/_mixins.scss:
@mixin input {
    padding: 10px;
    font-size: 17px;
    border-width: 0px;
    border-color: #6e926c;
    background-color: #ECECEC;
    border-style: solid;
    border-radius: 0 50px 50px 0px;
    box-shadow: 0px 0px 0px rgba(66,66,66,.0);
    text-shadow: -50px 0px 0px rgba(66,66,66,.0);
    &:focus {
        outline:none;
    }
}
@mixin flex-center {
    display: flex;
    justify-content: center;
    align-items: center;
}
@mixin flex-center-column {
    @include flex-center;
    flex-direction: column;
}

sources.sass/_loading.scss:
.custom-loader {
    width: 50px;
    height: 50px;
    display: grid;
}
.custom-loader::before,
.custom-loader::after {    
    content:"";
    grid-area: 1/1;
    --c: radial-gradient(farthest-side,rgb(255, 186, 58) 92%,#0000);
    background: 
      var(--c) 50%  0, 
      var(--c) 50%  100%, 
      var(--c) 100% 50%, 
      var(--c) 0    50%;
    background-size: 12px 12px;
    background-repeat: no-repeat;
    animation: s2 1s infinite;
}
.custom-loader::before {
  margin:4px;
  filter:hue-rotate(45deg);
  background-size: 8px 8px;
  animation-timing-function: linear
}

@keyframes s2{ 
  100%{transform: rotate(.5turn)}
}

sources.sass/_ingredient.scss:
.ingredient-contenaire{
    display: grid;
    justify-content: center;
    align-items: center;
    margin: 0;
    grid-template-columns: 1fr 35%;
    align-items: flex-start;
    grid-template-areas: 
    "A B";
    h2{
        color: $color-4;
        text-align: center;
    }
    .liste-ingredient{
        grid-area: B;
        height: 100vh;
        overflow: auto;

        input{
            display: none;
        }
        input:checked + label{
            background:$color-4;
            opacity: 1;
            color:$color-2;
        }
        label{
           display: block;
           font-size: 1.2rem;
           font-weight: bold;
           line-height:2rem;
           padding: 5px;
           text-transform: uppercase;
           opacity: 0.4;
           &:hover{
            opacity: 0.8;
           }
        }
    }
    .result-ingredient{
        grid-area: A;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        span{
            color: $color-1;
        }
        .cocktail-eacth{
            width: 200px;
            height: 200px;
            padding: 5px;
            h1{
                color: $color-2;

            }
        }
    }
}

sources.sass/_alphabet.scss:
.alphabet{
    display: grid;
    grid-template-columns: 1fr 35%;
    .alphabet-contenaire{
        height: 10%;
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        input{
            display: none;
        }
        input:checked{
            background: $color-2;
            color: #fff; 
        }
        label{
            display: block;
            font-size: 1.2rem;
            padding: 1rem;
            margin: 5px;
            background: $color-4;
            transition: 0.2s ease-in-out;
            &:hover{
                transform: scale(1.1);
                background: $color-2;
                color: #fff;
            }
        }
    }
}
