body {
  font-family: "Courier New", Courier, monospace;
  margin: 0;
  padding: 0;
}

#app {
  display: grid;
  grid-template-columns: 25% 1fr;
  grid-template-areas: "A B";
}

#main {
  grid-area: B;
}

ul {
  list-style: none;
}

#menu {
  grid-area: A;
  width: 25%;
  min-width: 250px;
  height: 100vh;
  margin: 0;
  background: #d3d3d3;
  position: fixed;
  display: grid;
  justify-content: center;
  grid-template-rows: 10% 1fr 30%;
  z-index: 1;
}
#menu .title h1 {
  font-weight: 100;
  margin-bottom: 0;
  font-size: 2rem;
}
#menu .title h1 span {
  font-weight: bold !important;
  color: #FF1818;
}
#menu .title article {
  letter-spacing: 0.2px;
  word-spacing: 0px;
  color: #1e8018;
  font-size: 1rem;
}
#menu .menu {
  display: flex;
  flex-direction: column;
  justify-content: center;
}
#menu .menu a {
  text-decoration: none;
  padding: 1rem;
  margin: 10px 0;
  transition: 0.5s ease-in-out;
}
#menu .menu a:hover {
  background: rgb(255, 166, 0);
  color: #000;
}
#menu .footer {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  font-size: 0.8rem;
}
#menu .footer a {
  color: #1e8018;
}
#menu .footer a:hover {
  color: #FF1818;
}
#menu .footer span {
  width: inherit;
}
#menu .footer span .signature {
  margin-bottom: 10px;
  text-align: center;
  font-weight: bold;
}
#menu .footer span a {
  font-size: 2rem;
  margin: 1rem;
}

.tirage {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}
.tirage .search {
  min-width: 400px;
  background: rgb(255, 166, 0);
  padding: 1rem;
  margin: 10px 0;
  right: 0;
}
.tirage .search p {
  margin: 10px 0 0 0;
  font-size: 1rem;
  color: #1e8018;
  opacity: 0.9;
}
.tirage .search span {
  margin: 10px;
}
.tirage .search span input[type=text] {
  padding: 10px;
  font-size: 17px;
  border-width: 0px;
  border-color: #6e926c;
  background-color: #ECECEC;
  border-style: solid;
  border-radius: 0 50px 50px 0px;
  box-shadow: 0px 0px 0px rgba(66, 66, 66, 0);
  text-shadow: -50px 0px 0px rgba(66, 66, 66, 0);
  border-radius: 50px 0 0 50px;
  background: #d3d3d3;
  letter-spacing: 0.1rem;
  color: #1e8018;
}
.tirage .search span input[type=text]:focus {
  outline: none;
}
.tirage .search span input[type=button] {
  padding: 10px;
  font-size: 17px;
  border-width: 0px;
  border-color: #6e926c;
  background-color: #ECECEC;
  border-style: solid;
  border-radius: 0 50px 50px 0px;
  box-shadow: 0px 0px 0px rgba(66, 66, 66, 0);
  text-shadow: -50px 0px 0px rgba(66, 66, 66, 0);
  border-radius: 0 50px 50px 0px;
  background: black;
  color: #d3d3d3;
  cursor: pointer;
}
.tirage .search span input[type=button]:focus {
  outline: none;
}
.tirage .search span input[type=button]:hover {
  transform: scale(1.1);
}
.tirage .search span input[type=button]:active {
  transform: scale(1);
}

.cocktail-contenaire {
  display: flex;
  flex-wrap: wrap;
  list-style: none;
  justify-content: center;
  align-items: center;
}
.cocktail-contenaire .cocktail-eacth {
  margin: 3px;
  padding: 5px;
  background: #d3d3d3;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}
.cocktail-contenaire .cocktail-eacth p {
  font-size: 0.8rem;
  font-weight: bold;
}
.cocktail-contenaire .cocktail-eacth h1 {
  color: #FF1818;
  font-size: 1rem;
  margin: 0;
}
.cocktail-contenaire .cocktail-eacth .name-contenaire {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.cocktail-contenaire .cocktail-eacth strong {
  color: #1e8018;
  text-decoration: underline;
}
.cocktail-contenaire .cocktail-eacth:hover {
  transform: scale(1.02);
  background: rgb(255, 166, 0);
}
.cocktail-contenaire .cocktail-eacth:hover h1 {
  color: black;
}
.cocktail-contenaire .cocktail-eacth img {
  width: 150px;
  height: 150px;
  margin: 5px;
}

.resultError {
  color: #FF1818;
}
.resultError span {
  font-weight: 100;
  color: rgb(255, 166, 0);
  font-size: 1.2rem;
}

.hasard {
  height: 100vh;
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
}
.hasard h1 {
  color: #1e8018;
}
.hasard .basic {
  background: #d3d3d3;
  padding: 0 2rem;
  transition: 0.5s ease-in-out;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}
.hasard .basic strong {
  color: #FF1818;
  font-size: 1rem;
  margin: 0;
}
.hasard .basic img {
  width: 400px;
  height: 400px;
  left: 50%;
}
.hasard .basic:hover {
  background: #000;
}
.hasard .basic:hover h1, .hasard .basic:hover h2, .hasard .basic:hover h3 {
  color: #fff;
}
.hasard .ingredient {
  background: rgb(255, 166, 0);
  padding: 1rem;
  height: auto;
}
.hasard .ingredient h1 {
  font-size: 1rem;
  position: relative;
}

.custom-loader {
  width: 50px;
  height: 50px;
  display: grid;
}

.custom-loader::before,
.custom-loader::after {
  content: "";
  grid-area: 1/1;
  --c: radial-gradient(farthest-side,rgb(255, 186, 58) 92%,#0000);
  background: var(--c) 50% 0, var(--c) 50% 100%, var(--c) 100% 50%, var(--c) 0 50%;
  background-size: 12px 12px;
  background-repeat: no-repeat;
  animation: s2 1s infinite;
}

.custom-loader::before {
  margin: 4px;
  filter: hue-rotate(45deg);
  background-size: 8px 8px;
  animation-timing-function: linear;
}

@keyframes s2 {
  100% {
    transform: rotate(0.5turn);
  }
}
.ingredient-contenaire {
  display: grid;
  justify-content: center;
  align-items: center;
  margin: 0;
  grid-template-columns: 1fr 35%;
  align-items: flex-start;
  grid-template-areas: "A B";
}
.ingredient-contenaire h2 {
  color: rgb(255, 166, 0);
  text-align: center;
}
.ingredient-contenaire .liste-ingredient {
  grid-area: B;
  height: 100vh;
  overflow: auto;
}
.ingredient-contenaire .liste-ingredient input {
  display: none;
}
.ingredient-contenaire .liste-ingredient input:checked + label {
  background: rgb(255, 166, 0);
  opacity: 1;
  color: #1e8018;
}
.ingredient-contenaire .liste-ingredient label {
  display: block;
  font-size: 1.2rem;
  font-weight: bold;
  line-height: 2rem;
  padding: 5px;
  text-transform: uppercase;
  opacity: 0.4;
}
.ingredient-contenaire .liste-ingredient label:hover {
  opacity: 0.8;
}
.ingredient-contenaire .result-ingredient {
  grid-area: A;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.ingredient-contenaire .result-ingredient span {
  color: #FF1818;
}
.ingredient-contenaire .result-ingredient .cocktail-eacth {
  width: 200px;
  height: 200px;
  padding: 5px;
}
.ingredient-contenaire .result-ingredient .cocktail-eacth h1 {
  color: #1e8018;
}

.alphabet {
  display: grid;
  grid-template-columns: 1fr 35%;
}
.alphabet .alphabet-contenaire {
  height: 10%;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
}
.alphabet .alphabet-contenaire input {
  display: none;
}
.alphabet .alphabet-contenaire input:checked {
  background: #1e8018;
  color: #fff;
}
.alphabet .alphabet-contenaire label {
  display: block;
  font-size: 1.2rem;
  padding: 1rem;
  margin: 5px;
  background: rgb(255, 166, 0);
  transition: 0.2s ease-in-out;
}
.alphabet .alphabet-contenaire label:hover {
  transform: scale(1.1);
  background: #1e8018;
  color: #fff;
}

