    @include flex-center-column;
    .search{
        min-width: 400px;
        background:$color-4;
        padding: 1rem;
        margin: 10px 0;
        right: 0;
        p{
            margin: 10px 0 0 0;
            font-size: 1rem;
            color: $color-2;
            opacity: 0.9;
        }
        span{
            margin: 10px;
            input[type ="text"]{
                @include input;
                border-radius: 50px 0 0 50px;
                background: $color-3;
                letter-spacing: 0.1rem;
                color: $color-2;
            }
            input[type ="button"]{
               @include input;
               border-radius: 0 50px 50px 0px;
               background: black;
               color: $color-3;
               cursor: pointer;
               &:hover{
                transform: scale(1.1)
               }
               &:active{
                transform: scale(1)
               }
            }
        }
    }

}
