    flex-wrap: wrap;
    list-style: none;
    justify-content: center;
    align-items: center;
    .cocktail-eacth{
        margin: 3px;
        padding: 5px;
        background: $color-3;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        p{
            font-size: 0.8rem;
            font-weight: bold;
        }
        h1{
            color: $color-1;
            font-size: 1rem;
            margin: 0;
        }
        .name-contenaire{
            @include flex-center-column;
            align-items: center;
            justify-content: center;

        }
        strong{
            color: $color-2;
            text-decoration: underline;
        }
        &:hover{
            transform: scale(1.02);
            background: $color-4;
            h1{
                color: black;
            }
        }
        img{
            width: 150px;
            height: 150px;
            margin: 5px;
        }
    }

}
.resultError{
    color: $color-1;
    span{
        font-weight: 100;
        color: $color-4;
        font-size: 1.2rem;
    }
}
